import React, {useState} from'react'
import {ethers} from 'ethers'
import { Link } from "react-router-dom";
import Register from '../register/Register';


function HomeSix(props) {
    const protable = document.getElementById('product-table');

    const {contract, setContract} = props;
    const [buttonText, setbuttonText] = useState('Connect Provider/Signer')

    const setData = async() =>{
        for(let num = 0; num <3;num++){
            let tr = document.createElement('tr');
            var td = document.createElement('td');
            td.textContent = num;
            tr.appendChild(td);

            var td = document.createElement('td');
            td.textContent = await contract.readProductName(num);
            tr.appendChild(td);

            var td = document.createElement('td');
            td.textContent = await contract.readClassNum(num);
            tr.appendChild(td);

            var td = document.createElement('td');
            td.textContent = await contract.readColor(num);
            tr.appendChild(td);

            var td = document.createElement('td');
            td.textContent = await contract.readCompany(num);
            tr.appendChild(td);

            var td = document.createElement('td');
            td.textContent = await contract.readManuDate(num);
            tr.appendChild(td);

            var td = document.createElement('td');
            td.textContent = await contract.readPurDate(num);
            tr.appendChild(td);

            protable.appendChild(tr);

        }
    }



    const setTable = async() =>{
        var messageStatus = document.getElementById('messageStatus');
        let  res = await contract.readProductName(1);
        messageStatus.innerHTML = res
        //setbuttonText(res);	


    }


    return(
        <>
        <h1>譲渡アプリ</h1>
        <table id="product-table">
            <thread>
                <tr>
                    <th>商品番号</th>
                    <th>商品名</th>
                    <th>商品分類</th>
                    <th>色</th>
                    <th>製造会社</th>
                    <th>製造日</th>
                    <th>購入日</th>
                </tr>
            </thread>
        </table>
        <p id="messageStatus" >a</p>   
        <button id="retrieveButton" onClick={setTable}>Retrieve</button>

        <div>

            登録は<Link to={`../register`} >こちら</Link>

        </div>
        <div>
            貸出は<Link to={`../borrow`}>こちら</Link>

        </div>
        {setTable}
        </>
    );



}
//<Register contract={contract}/>


export default HomeSix;
