import { Link } from "react-router-dom";
import React, {useState} from'react'
import HomeTwo from './HomeTwo';
import HomeThree from './HomeThree';

const Home = () => {

    const [errorMessage, setErrorMessage] = useState(null);
    const [defaultAccount, setDefaultAccount] = useState(null);
	const [connButtonText, setConnButtonText] = useState('Connect Wallet');

    const [provider, setProvider] = useState(null);
	const [signer, setSigner] = useState(null);
	const [contract, setContract] = useState(null);

    const [HomeTwo, allowHomeTwo] = useState(false);
    const [HomeThree, allowHomeThree] = useState(false);

    const connectWalletHandler = () => {
        if (window.ethereum && window.ethereum.isMetaMask) {
			window.ethereum.request({ method: 'eth_requestAccounts'})
			.then(result => {
				accountChangedHandler(result[0]);
                console.log("window.ethereum is called to check if wallet is installed in your browser, and connects to this page");
                console.log("Address obtained from wallet - " + result[0]);
				setConnButtonText('Wallet Connected');

			})
			.catch(error => {
				setErrorMessage(error.message);
			});

		} else {
			console.log('Need to install MetaMask');
			setErrorMessage('Please install MetaMask browser extension to interact');
		}
    };

    const accountChangedHandler = (newAccount) => {
		setDefaultAccount(newAccount);
	}

  return (
    <>
      <h1>ホーム</h1>
      <div>
        <h3>Connect your wallet</h3>
        <button onClick={connectWalletHandler}>{connButtonText}</button>
        <p>Wallet Address - {defaultAccount}</p>
      </div>
      <div>
        <HomeTwo allowHomeTwo={HomeTwo} setProvider={setProvider} setSigner={setSigner} allowStepThree={allowHomeThree}/>
      </div>
      <div>
        <HomeThree allowHomeThree={HomeThree} signer={signer} setContract={setContract} />
      </div>
      <div>
        譲渡品登録は<Link to={`/register/`}>こちら</Link>
      </div>
      <div>
        貸出登録は<Link to={`/borrow/`}>こちら</Link>
      </div>
    </>
  );
};

export default Home;
